package com.syntax.class07;

public class WhileLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=2;
		while(i<=20) {
			System.out.print(i+" ");
			i++;
		}
		System.out.println();
		System.out.println("The value of i outside of while loop = " +i);
		int b=1;
		while(b<=10) {
		System.out.print(b+" ");
		b++;
		
		
		int c=20;
		while(c>=1) {
		System.out.print(c+" ");
		c--;
		
		
		
		
		
		
		
	}}

}}
